package org.Collegeinfo;
// IF statement program
public class Ifstat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 15;
		if (a == 15) {
			System.out.println("a value is correct");
		}
		if (a == 10) {
			System.out.println("a value is correct");

		} else {
			System.out.println("a value is wrong");
		}
	}

}
